import React from 'react';
import { Carousel, Toast } from 'antd-mobile';
import createHistory from 'history/createHashHistory';
import * as actions from '../actions/corrceting';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import Utils from 'Utils';
import { StorageKeys } from 'kCommon/constants';
import { Base } from '../../../../component/exercise';
import { Answer } from '../../../../component/exercise';
console.log(Utils, 'UtilsUtils');
const history = createHistory();

import '../less/correcting.less';

class Correcting extends React.Component {
  lastX = 0; //滑动开始x轴位置
  lastY = 0; //滑动开始y轴位置
  currentGesture = 0; //标识手势
  iSipad = navigator.userAgent.toLowerCase().indexOf('education') != -1;
  exerciseId = '';
  previewId = '';
  clientType = Utils.storage.get(StorageKeys.CLIENTTYPE); //pad:平板  phone:手机  web:电脑
  _time = 0;
  constructor(props) {
    super(props);
    this.state = {
      currIndex: '',
      visible: false,
      headerName: '练习'
    };
  }
  componentDidMount() {
    this.props.actions.getQuesList();
  }
  render() {
    const questionInfoList = this.props.questionInfoList || [];
    return (
      <div className="correcting-wrap">
        <Carousel
          dots={false}
          selectedIndex={this.state.currIndex}
          className="correcting">
          {questionInfoList.map((item, index) => (
            <span key={index}>
              <Base question={item} />
              <Answer question={item} />
            </span>
          ))}
        </Carousel>
      </div>
    );
  }
}

export default connect(
  state => {
    return { questionInfoList: state.indexReducer.ExamLoad.questionInfoList };
  },
  dispatch => {
    return { actions: bindActionCreators(actions, dispatch) };
  }
)(Correcting);
