import React from 'react';
import RouteWithSubRoutes from '../router';


export default function SubLayout(props) {
    props.routes && props.routes.forEach((item, i, arr) => {
        if (item.indexRoute && !item.isAdd) {
            if (arr[i].routes) {
                arr[i].routes.unshift({
                    title: item.title,
                    path: '',
                    exact: true,
                    dir: '',
                    component: item.indexRoute,
                });
                delete arr[i].indexRoute;
            }
            arr[i].isAdd = true;
        }
        arr[i].dir = props.match ? props.match.path : '';
    });

    return (
        <div>
            {props.routes && props.routes.map((route, i) => (<RouteWithSubRoutes key={i} {...route} />))}
        </div>
    );
}
