import { combineReducers } from 'redux'
import { routerReducer } from 'react-router-redux'

// const context = require.context('../modules', true, /\/*\/reducers\/((?!\/)[\s\S])+\.js$/)         //  获取reducers下的所有js文件
const context = require.context('../modules', true, /\/*\/reducers\/index\.js$/)   
console.log(context)                   //  获取reducers下的index.js文件
// const keys = context.keys().filter(item => item !== './index.js')
const keys = context.keys() || [];
// console.log(keys);
const reducers = {}
for (let i = 0; i < keys.length; i++) {
  let exp = context(keys[i])
  for (let fn in exp) {
    reducers[fn] = exp[fn]
  }
}

const rootReducer = combineReducers({
  router: routerReducer,
  ...reducers,
})

export default rootReducer
