import React from 'react';
import { Route } from 'react-router-dom';
import SubLayout from '../subLayout';


const nodePath = require('path');

export const RouteWithSubRoutes = (route) => {
    const path = nodePath.normalize(`${route.dir}${route.path}`);
    const Component = route.component ? route.component : route.indexRoute ? route.indexRoute : SubLayout;
    return (
        <Route path={path} exact={route.exact ? true : false} render={props => (<Component {...props} routes={route.routes} />)} />
    );
};
