import CommonIndex from './../modules/index';
import Correcting from './../modules/index/container/correcting';
const context = require.context('./../modules', true, /\/*\/Route\.js$/);
console.log(context)
const keys = context.keys();
// const keys = context.keys().filter(item => item !== './index.js')
const routes = [
  {
    title: '',
    path: '/',
    exact: true,
    component: CommonIndex
  },
  {
    title: '作业',
    path: '/correcting',
    component: Correcting
  }
];
for (let i = 0; i < keys.length; i++) {
  console.log(context(keys[i]))
  routes.push(context(keys[i]));
}
console.log(routes)

export default routes;

/**
 * title 路由名称
 * path 路由地址
 * exact 路由渲染完全匹配
 * component 路由组件/模板组件（ex:导航栏等）
 * indexRoute 默认渲染组件
 * routes 子路由
 */

